-- ============================================================
-- 94Manage Demo Seed Data
-- Run: psql $DATABASE_URL -f demo-seed.sql
-- Creates 20-50 records per entity for demo tenant
-- ============================================================

-- Demo Constants
-- TENANT_1: 11111111-1111-1111-1111-111111111111
-- BRANCH_1: a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d

-- ============================================================
-- 0. Ensure demo tenant & branch exist
-- ============================================================
INSERT INTO tenants (id, name, status, settings, trial_status, trial_start_at, trial_end_at)
VALUES (
  '11111111-1111-1111-1111-111111111111',
  '蜂神榜示範補習班',
  'active',
  '{"plan": "pro"}'::jsonb,
  'approved',
