-- Add tenant_id to branches if column exists
DO $$ BEGIN
  ALTER TABLE branches ADD COLUMN IF NOT EXISTS tenant_id UUID REFERENCES tenants(id);
  UPDATE branches SET tenant_id = '11111111-1111-1111-1111-111111111111'
  WHERE id = 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d' AND tenant_id IS NULL;
EXCEPTION WHEN OTHERS THEN NULL;
END $$;

