INSERT INTO conversations (tenant_id, branch_id, channel, intent, query, answer, model, latency_ms, tokens_used, created_at) VALUES
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'FAQ', '明天有上課嗎？', '明天（週三）有兩堂課：16:00-18:00 國中國文作文班、18:30-21:00 高中化學實驗班。', 'flash-lite', 420, 85, NOW() - INTERVAL '6 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'attendance', '王小明今天有來嗎？', '王小明今天出席紀錄為「已到」。', 'flash-lite', 350, 72, NOW() - INTERVAL '5 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'billing', '陳小強繳費了嗎？', '陳小強 2月份物理精修班學費 $7,000 目前為「待繳」狀態。', 'flash', 580, 120, NOW() - INTERVAL '4 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'schedule', '這週五有什麼課？', '週五有兩堂課：\n1. 16:00-17:30 國小英文啟蒙班（劉心怡老師）\n2. 18:00-20:00 國中英文文法班（林志明老師）', 'flash-lite', 390, 95, NOW() - INTERVAL '3 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'line', 'billing', '我要幫小明繳 2 月學費', '已確認：王小明 國小數學基礎班 2月份學費 $4,500。請確認是否記錄繳費？', 'flash', 620, 135, NOW() - INTERVAL '2 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'greeting', '嗨你好', '您好！我是蜂神榜 AI 助手，可以幫您查詢出勤、成績、繳費等資訊。請問有什麼需要幫忙的嗎？', 'flash-lite', 280, 60, NOW() - INTERVAL '1 day'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'FAQ', '補習班地址在哪？', '台北總校地址：台北市大安區。營業時間為週一至週六 14:00-21:00。', 'flash-lite', 310, 68, NOW() - INTERVAL '1 day 2 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'attendance', '李小華最近出席狀況如何？', '李小華近兩週出席紀錄：出席 3 次，缺席 0 次，出席率 100%。表現良好！', 'flash', 450, 98, NOW() - INTERVAL '1 day 5 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'line', 'FAQ', '請問學費多少？', '各班學費如下：\n- 國小數學基礎班：月費 $4,500\n- 國中英文會話班：月費 $5,500\n- 高中物理精修班：月費 $7,000\n可季繳或學期繳享折扣。', 'flash', 520, 140, NOW() - INTERVAL '2 days'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'enrollment', '我想幫小孩報名數學班', '好的！目前國小數學基礎班還有名額。請提供孩子的姓名、年級，我可以為您處理報名。', 'sonnet', 680, 155, NOW() - INTERVAL '2 days 3 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'billing', '這個月還有誰沒繳費？', '2月份待繳費學生共 7 位：\n1. 陳小強 - 物理精修班 $7,000\n2. 黃小豪 - 數學衝刺班 $6,000\n3. 吳小傑 - 英文會話班 $5,500\n（及其他 4 位）', 'flash', 750, 180, NOW() - INTERVAL '3 days'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'web', 'schedule', '下週一有幾堂課？', '下週一（3/3）排課如下：\n1. 16:00-18:00 國小數學基礎班（謝佳穎老師）\n2. 18:30-20:30 國中英文會話班（林志明老師）', 'flash-lite', 360, 88, NOW() - INTERVAL '3 days 4 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'FAQ', '請問有暑期班嗎？', '有的！暑期班預計 7 月開課，課程內容包含數學先修、英文閱讀、科學實驗營等。詳細課表約 5 月公布，歡迎先登記候補。', 'flash', 480, 110, NOW() - INTERVAL '4 days'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'line', 'attendance', '張小美上週有缺課嗎？', '張小美上週出席紀錄：出席 1 次，缺席 1 次（2/24 因牙醫看診請假）。出席率 50%。', 'flash', 420, 92, NOW() - INTERVAL '4 days 2 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'billing', '幫劉小琪繳學費 4500', '已記錄：劉小琪 國小數學基礎班 2月份學費 $4,500，繳費方式：現金。感謝您！', 'flash', 590, 128, NOW() - INTERVAL '5 days'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'FAQ', '停課通知怎麼發？', '您可以在系統設定 → 通知管理中發送停課通知。支援 Telegram、LINE 推播，也可以設定自動通知模板。', 'flash', 440, 100, NOW() - INTERVAL '5 days 6 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'web', 'FAQ', '如何新增學生？', '請至「學員管理」→「新增學員」，填入姓名、年級、聯絡電話等基本資料即可。也可以透過 Telegram Bot 直接說「新增學生 王小明 五年級」。', 'flash', 510, 115, NOW() - INTERVAL '6 days'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'schedule', '謝佳穎老師這週幾堂課？', '謝佳穎老師本週排課 2 堂：\n1. 週一 16:00-18:00 國小數學基礎班\n2. 週二 18:30-21:00 國中數學衝刺班\n總計 5 小時。', 'flash', 460, 105, NOW() - INTERVAL '6 days 3 hours'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'line', 'enrollment', '蔡小宇想轉到進階班', '好的，蔡小宇目前就讀「國小數學進階班」。如需轉班請至學員管理頁面操作，或告訴我目標班級名稱。', 'sonnet', 720, 160, NOW() - INTERVAL '7 days'),
  ('11111111-1111-1111-1111-111111111111', 'a1b2c3d4-e5f6-1a2b-8c3d-4e5f6a7b8c9d', 'telegram', 'FAQ', '可以用信用卡繳費嗎？', '目前支援的繳費方式有：現金、銀行轉帳、LINE Pay。信用卡繳費功能開發中，預計下季上線。', 'flash-lite', 340, 78, NOW() - INTERVAL '7 days 5 hours');

